    CacheMonitorScheduler statusbarScheduler;

    class CacheMonitorScheduler {

        public void start() {
            Timer timer = new Timer("Mindliner Cache Monitor");
            timer.schedule(new MonitorTask(), new Date(), 1000 * 2);
        }

        class MonitorTask extends TimerTask {

            @Override
            public void run() {

                SwingUtilities.invokeLater(new Runnable() {
                    @Override
                    public void run() {
                        CacheDetailObjectsLabel.setText(Integer.toString(CacheEngineStatic.getObjectCount()));
                        CacheDetailsWorkSlotsLabel.setText(Integer.toString(CacheEngineStatic.getWorkUnitCount()));
                        CacheDetailsLinksLabel.setText(Integer.toString(CacheEngineStatic.getLinksCount()));
                        CacheDetailsRootPath.setText(CacheEngineStatic.getCacheDirectoryForCurrentUser());
                    }
                });
            }
        }
    }

