    private void OpenCacheFolderButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_OpenCacheFolderButtonActionPerformed
        try {
            Desktop.getDesktop().open(new File(CacheEngineStatic.getCacheDirectoryForCurrentUser()));
        } catch (IOException ex) {
            JOptionPane.showMessageDialog(this, ex, "Cache Location", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_OpenCacheFolderButtonActionPerformed

